mapper.select:
select * from (select tmp_tb.* , rownum rn from (SELECT ADMINSSIONSPLANINFORMATION."CreateTime", ADMINSSIONSPLANINFORMATION."CreateYear", ADMINSSIONSPLANINFORMATION."AdminssionsNumber", SCHOOLINFORMATION."SchoolName", MAJORINFORMATION."MajorName", SCHOOLINFORMATION."ProvinceName", ADMINSSIONSPLANINFORMATION."PublishTime" from ADMINSSIONSPLANINFORMATION ,SCHOOLINFORMATION , MAJORINFORMATION WHERE SCHOOLINFORMATION."SchoolName" LIKE '%${schoolname}%' AND MAJORINFORMATION."MajorName" LIKE '%${majorname}%' AND SCHOOLINFORMATION."ProvinceName" LIKE '%${provincename}%' AND ADMINSSIONSPLANINFORMATION."MajorKey"=MAJORINFORMATION."MajorKey" AND ADMINSSIONSPLANINFORMATION."SchoolKey"=SCHOOLINFORMATION."SchoolKey" AND ADMINSSIONSPLANINFORMATION."IsPublish"=1 AND ADMINSSIONSPLANINFORMATION."CreateYear"=(Select Extract(year from sysdate) from dual) ORDER BY "CreateTime" ASC
${pager.orderCondition}
)tmp_tb where rownum <= ${pager.endIndex}) where rn>= ${pager.startIndex}
