mapper.select:
select * from (select tmp_tb.* , rownum rn from (SELECT ADMINSSIONSPLANINFORMATION."CreateTime", ADMINSSIONSPLANINFORMATION."CreateYear", ADMINSSIONSPLANINFORMATION."AdminssionsNumber", SCHOOLINFORMATION."SchoolName", MAJORINFORMATION."MajorName", SCHOOLINFORMATION."ProvinceName", ADMINSSIONSPLANINFORMATION."PublishTime", ADMINSSIONSPLANINFORMATION."OfflineTime" from ADMINSSIONSPLANINFORMATION ,SCHOOLINFORMATION , MAJORINFORMATION WHERE SCHOOLINFORMATION."SchoolName" LIKE '%${schoolname}%' AND MAJORINFORMATION."MajorName" LIKE '%${majorname}%' AND SCHOOLINFORMATION."ProvinceName" LIKE '%${provincename}%' AND ADMINSSIONSPLANINFORMATION."MajorKey"=MAJORINFORMATION."MajorKey" AND ADMINSSIONSPLANINFORMATION."SchoolKey"=SCHOOLINFORMATION."SchoolKey" AND ADMINSSIONSPLANINFORMATION."IsPublish"=1 AND ADMINSSIONSPLANINFORMATION."CreateYear"=(Select Extract(year from sysdate) from dual) AND ADMINSSIONSPLANINFORMATION."OfflineTime">(select sysdate from dual) ORDER BY "CreateTime" ASC
${pager.orderCondition}
)tmp_tb where rownum <= ${pager.endIndex}) where rn>= ${pager.startIndex}

select from (select tmp_tb.* , rownum rn from (SELECT DISTINCT FROM ADMINSSIONSPLANINFORMATION a left join MAJORINFORMATION m on a."MajorKey" = m."MajorKey" left join SCHOOLINFORMATION s on a."SchoolKey" = s."SchoolKey" WHERE 1=1 AND "CreateYear" = #{createyear}
AND "SchoolName" LIKE '%'||#{schoolkey}||'%'
AND "MajorName" LIKE '%'||#{majorkey}||'%'
AND "ProvinceName" LIKE '%'||#{adminssionskey}||'%'
ORDER BY "CreateTime" DESC ) tmp_tb where rownum <= ${pager.endIndex}) where rn >= ${pager.startIndex}

select "AdminssionsKey" from ADMINSSIONSPLANINFORMATION
WHERE
"MajorKey" = #{majorkey}
and "SchoolKey" = #{schoolkey}
and "CreateYear" = #{createyear}

select  from
(select tmp_tb.* , rownum rn from
(select  from ADMINSSIONSPLANINFORMATION a
left join MAJORINFORMATION m on a."MajorKey" = m."MajorKey"
left join SCHOOLINFORMATION s on a."SchoolKey" = s."SchoolKey"
WHERE "CreateYear" = #{createyear}
ORDER BY "CreateTime" DESC
) tmp_tb where rownum <= ${pager.endIndex})
where rn >= ${pager.startIndex}
